import React from 'react'
import Footer from './Footer'
import Header from './Header'
import Main from './Main'
class App extends React.Component {
  state = {
    msg: '',
  }
  getChildMsg = (msg) => {
    console.log(msg)
  }
  receiveMsg = (msg) => {
    this.setState({
      msg,
    })
  }
  render() {
    return (
      <div>
        <Header openLogin={this.receiveMsg}></Header>
        <Main getMsg={this.getChildMsg}></Main>
        <Footer></Footer>
      </div>
    )
  }
}
export default App
