import React from 'react'
import Footer from './Footer'
import Header from './Header'
import Main from './Main'
class App extends React.Component {
  state = {
    openMain: true,
    email: undefined,
  }
  getChange = (msg) => {
    console.log(msg)
    this.setState({
      openMain: msg,
    })
  }
  getEmail = (isLogin) => {
    if (isLogin) {
      this.setState({
        email: JSON.parse(localStorage.getItem('user')).email,
      })
    }
  }
  render() {
    return (
      <div>
        <Header getChange={this.getChange}></Header>
        <Main open={this.state.openMain} getChange={this.getChange}></Main>
        <Footer></Footer>
      </div>
    )
  }
}
export default App
