import React from 'react'
import '../Style/main.css'
class Main extends React.Component {
  state = {
    emailaddr: '',
    pw: '',
    show: 'show',
    showText: 'Show',
    emailTip: '',
  }
  getInfo = () => {
    let obj = this.state
    console.log(obj)
    this.setState({
      emailaddr: '',
      pw: '',
    })
  }
  handleChange = (e) => {
    const { name, value } = e.target
    this.setState({
      [name]: value,
    })
  }
  showPw = () => {
    const { show } = this.state
    if (show === 'show') {
      this.setState({
        show: 'hidden',
        showText: 'Hide',
      })
    } else {
      this.setState({ show: 'show', showText: 'Show' })
    }
  }
  verifyEmail = () => {
    const { emailaddr } = this.state
    let reg = /^[a-zA-Z0-9_-]+@[a-zA-Z0-9_-]+(\.[a-zA-Z0-9_-]+)+$/
    if (!reg.test(emailaddr)) {
    }
  }
  render() {
    return (
      <div className="mainbody">
        <div className="login">
          <h1>Sign in to your account</h1>
          <div className="forminput">
            <p>Email</p>
            <input
              type="email"
              name="emailaddr"
              value={this.state.emailaddr}
              onChange={this.handleChange}
              onBlur={this.verifyEmail}
            />
            <span className="tip">{this.state.emailTip}</span>
            <p>Password</p>
            <input
              type={this.state.show === 'show' ? 'password' : 'text'}
              name="pw"
              value={this.state.pw}
              onChange={this.handleChange}
            />
            <span className="show_pw" onClick={this.showPw}>
              {this.state.showText}
            </span>
            <button className="submit" onClick={this.getInfo}>
              Sign In
            </button>
            <div className="more_info">
              <span>Don't have an account?</span>
              <a href="#">Sign up</a>
              <a href="#">Forgot password? </a>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
export default Main
